import React, { useState } from "react";
import "./TravelRegistrationForm.css";

const TravelRegistrationForm = () => {
  const [flightNumber, setFlightNumber] = useState("");
  const [arrivalTime, setArrivalTime] = useState("");
  const [travelMethod, setTravelMethod] = useState("Flight");

  const handleSubmit = (e) => {
    e.preventDefault();
    console.log({ travelMethod, flightNumber, arrivalTime });
  };

  return (
    <div className="container">
      <h2 className="title">Travel Registration</h2>
      <form onSubmit={handleSubmit}>
        <label>Travel Method</label>
        <select
          className="input"
          value={travelMethod}
          onChange={(e) => setTravelMethod(e.target.value)}
        >
          <option value="Flight">Flight</option>
          <option value="Train">Train</option>
          <option value="Car">Car</option>
        </select>

        {travelMethod === "Flight" && (
          <>
            <label>Flight Number</label>
            <input
              type="text"
              className="input"
              value={flightNumber}
              onChange={(e) => setFlightNumber(e.target.value)}
              placeholder="Enter flight number"
              required
            />

            <label>Arrival Time</label>
            <input
              type="datetime-local"
              className="input"
              value={arrivalTime}
              onChange={(e) => setArrivalTime(e.target.value)}
              required
            />
          </>
        )}

        <button type="submit" className="button register">Register Travel</button>
      </form>
    </div>
  );
};

export default TravelRegistrationForm;
