import React from 'react'
import { useState } from "react";
import axios from "axios";
import "./ConferenceForm.css";

const ConferenceForm = () => {

    const [formData, setFormData] = useState({
        email: "",
        password: "",
        travel_method: "",
        flight_no: "",
        arrival_time: "",
        dietary_requirements: "",
        sessions: "",
        expenses: ""
      });

      const handleChange = (e) => {
        setFormData({ ...formData, [e.target.name]: e.target.value });
      };

      const handleRegister = async () => {
        try {
          await axios.post("http://localhost:8000/register", {
            email: formData.email,
            password: formData.password,
            role: "attendee",
          });
          alert("Registration successful!");
        } catch (error) {
          alert("Registration failed");
        }
      };

      const handleSubmit = async () => {
        try {
          await axios.post("http://localhost:8000/attendance", formData);
          alert("Attendance recorded successfully!");
        } catch (error) {
          alert("Submission failed");
        }
      };


  return (
    <div className='container'>
        <h2 className="title">Conference Registration</h2>
        <input name="email" placeholder="Email" className="input" onChange={handleChange} />
        <input name="password" type="password" placeholder="Password" className="input" onChange={handleChange} />
        <button onClick={handleRegister} className="button register">Register</button>
        <h2 className="title">Submit Attendance</h2>
        <input name="travel_method" placeholder="Travel Method" className="input" onChange={handleChange} />
        <input name="flight_no" placeholder="Flight No" className="input" onChange={handleChange} />
        <input name="arrival_time" placeholder="Arrival Time" className="input" onChange={handleChange} />
        <input name="dietary_requirements" placeholder="Dietary Requirements" className="input" onChange={handleChange} />
        <input name="sessions" placeholder="Sessions (comma-separated)" className="input" onChange={handleChange} />
        <input name="expenses" placeholder="Expenses" className="input" onChange={handleChange} />
        <button onClick={handleSubmit} className="button submit">Submit</button>
    </div>
  )
}

export default ConferenceForm
