// AdminDashboard.jsx
import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { saveAs } from 'file-saver';
import { utils, write } from 'xlsx';
import './AdminDashboard.css';

const AdminDashboard = () => {
  const [loading, setLoading] = useState(true);
  const [attendees, setAttendees] = useState([]);
  const [dietarySummary, setDietarySummary] = useState(null);
  const [sessionStats, setSessionStats] = useState([]);
  const [vipTravel, setVipTravel] = useState({ vipTravel: [], issues: [] });
  const [activeTab, setActiveTab] = useState('attendees');
  const [showIssueModal, setShowIssueModal] = useState(false);
  const [selectedIssue, setSelectedIssue] = useState(null);
  const [exportLoading, setExportLoading] = useState(false);

  useEffect(() => {
    fetchDashboardData();
  }, []);

  const fetchDashboardData = async () => {
    setLoading(true);
    try {
      // Fetch all required data
      const [attendeesRes, dietaryRes, sessionsRes, vipTravelRes] = await Promise.all([
        axios.get('/api/admin/attendees'),
        axios.get('/api/admin/dietary-summary'),
        axios.get('/api/admin/session-stats'),
        axios.get('/api/admin/vip-travel')
      ]);

      setAttendees(attendeesRes.data);
      setDietarySummary(dietaryRes.data);
      setSessionStats(sessionsRes.data);
      setVipTravel(vipTravelRes.data);

    } catch (error) {
      console.error('Error fetching dashboard data:', error);
    } finally {
      setLoading(false);
    }
  };

  const handleExportData = (data, filename) => {
    setExportLoading(true);

    try {
      const worksheet = utils.json_to_sheet(data);
      const workbook = utils.book_new();
      utils.book_append_sheet(workbook, worksheet, 'Data');

      const excelBuffer = write(workbook, { bookType: 'xlsx', type: 'array' });
      const blob = new Blob([excelBuffer], { type: 'application/vnd.openxmlformats-officedocument.spreadsheetml.sheet' });

      saveAs(blob, `${filename}-${new Date().toISOString().slice(0, 10)}.xlsx`);
    } catch (error) {
      console.error('Error exporting data:', error);
    } finally {
      setExportLoading(false);
    }
  };

  const handleIssueClick = (issue) => {
    setSelectedIssue(issue);
    setShowIssueModal(true);
  };

  // Attendance Statistics Card
  const renderAttendanceStats = () => {
    if (!attendees.length) return null;

    const totalAttendees = attendees.length;
    const vipCount = attendees.filter(a => a.is_vip).length;
    const withTravel = attendees.filter(a => a.travel_type).length;
    const withDietary = attendees.filter(a => a.has_dietary).length;

    // Count by region
    const regionCounts = attendees.reduce((acc, attendee) => {
      acc[attendee.region] = (acc[attendee.region] || 0) + 1;
      return acc;
    }, {});

    return (
      <div className="card mb-4">
        <div className="card-header">
          <h5 className="mb-0">Attendance Summary</h5>
        </div>
        <div className="card-body">
          <div className="row">
            <div className="col-md-6">
              <table className="table striped bordered">
                <tbody>
                  <tr>
                    <td>Total Attendees</td>
                    <td>{totalAttendees}</td>
                  </tr>
                  <tr>
                    <td>VIP Attendees</td>
                    <td>{vipCount} ({((vipCount/totalAttendees) * 100).toFixed(1)}%)</td>
                  </tr>
                  <tr>
                    <td>With Travel Info</td>
                    <td>{withTravel} ({((withTravel/totalAttendees) * 100).toFixed(1)}%)</td>
                  </tr>
                  <tr>
                    <td>With Dietary Requirements</td>
                    <td>{withDietary} ({((withDietary/totalAttendees) * 100).toFixed(1)}%)</td>
                  </tr>
                </tbody>
              </table>
            </div>
            <div className="col-md-6">
              <h6>Attendance by Region</h6>
              {Object.entries(regionCounts).map(([region, count]) => (
                <div key={region} className="mb-2">
                  <div className="d-flex justify-content-between">
                    <span>{region}</span>
                    <span>{count} ({((count/totalAttendees) * 100).toFixed(1)}%)</span>
                  </div>
                  <div className="progress-bar-container">
                    <div 
                      className={`progress-bar ${region === 'US' ? 'progress-primary' : region === 'Europe' ? 'progress-success' : 'progress-info'}`}
                      style={{width: `${(count/totalAttendees) * 100}%`}} 
                    />
                  </div>
                </div>
              ))}
            </div>
          </div>
        </div>
      </div>
    );
  };

  // Attendee List Tab
  const renderAttendeesList = () => {
    return (
      <>
        <div className="d-flex justify-content-between mb-3">
          <h4>Conference Attendees</h4>
          <button 
            className="btn outline-success" 
            onClick={() => handleExportData(attendees, 'attendees-list')}
            disabled={exportLoading}
          >
            {exportLoading ? <div className="spinner-sm" /> : 'Export to Excel'}
          </button>
        </div>

        <div className="table-responsive">
          <table className="table striped hover">
            <thead>
              <tr>
                <th>Name</th>
                <th>Email</th>
                <th>Region</th>
                <th>Status</th>
                <th>Travel</th>
                <th>Sessions</th>
                <th>Dietary</th>
              </tr>
            </thead>
            <tbody>
              {attendees.map(attendee => (
                <tr key={attendee.user_id}>
                  <td>
                    {attendee.first_name} {attendee.last_name}
                    {attendee.is_vip && <span className="badge warning ms-2">VIP</span>}
                  </td>
                  <td>{attendee.email}</td>
                  <td>{attendee.region}</td>
                  <td>
                    <span className={`badge ${attendee.travel_type ? 'success' : 'danger'}`}>
                      {attendee.travel_type ? 'Confirmed' : 'Incomplete'}
                    </span>
                  </td>
                  <td>
                    {attendee.travel_type ? (
                      <small>
                        {attendee.travel_type}: {attendee.airline} {attendee.flight_number}<br />
                        {new Date(attendee.departure_time).toLocaleString()}
                      </small>
                    ) : (
                      <span className="badge secondary">Not Provided</span>
                    )}
                  </td>
                  <td>
                    {attendee.registered_sessions > 0 ? (
                      <span className="badge success">{attendee.registered_sessions} Sessions</span>
                    ) : (
                      <span className="badge danger">None</span>
                    )}
                  </td>
                  <td>
                    {attendee.has_dietary ? (
                      <span className="badge info">Provided</span>
                    ) : (
                      <span className="badge secondary">None</span>
                    )}
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      </>
    );
  };

  // Dietary Requirements Tab
  const renderDietaryRequirements = () => {
    if (!dietarySummary) return null;

    const { summary, specialRequests } = dietarySummary;

    return (
      <>
        <div className="d-flex justify-content-between mb-3">
          <h4>Dietary Requirements</h4>
          <button 
            className="btn outline-success" 
            onClick={() => handleExportData(
              [...Object.entries(summary).map(([key, value]) => ({ requirement: key, count: value })),
               ...specialRequests]
              , 'dietary-requirements')}
            disabled={exportLoading}
          >
            {exportLoading ? <div className="spinner-sm" /> : 'Export to Excel'}
          </button>
        </div>

        <div className="row">
          <div className="col-md-6">
            <div className="card mb-4">
              <div className="card-header">
                <h5 className="mb-0">Dietary Restrictions Summary</h5>
              </div>
              <div className="card-body">
                <table className="table striped bordered">
                  <thead>
                    <tr>
                      <th>Requirement</th>
                      <th>Count</th>
                      <th>Percentage</th>
                    </tr>
                  </thead>
                  <tbody>
                    {Object.entries(summary).map(([requirement, count]) => (
                      <tr key={requirement}>
                        <td>{requirement}</td>
                        <td>{count}</td>
                        <td>{((count / attendees.length) * 100).toFixed(1)}%</td>
                      </tr>
                    ))}
                  </tbody>
                </table>
              </div>
            </div>
          </div>
          <div className="col-md-6">
            <div className="card mb-4">
              <div className="card-header">
                <h5 className="mb-0">Special Requests</h5>
              </div>
              <div className="card-body">
                {specialRequests.length > 0 ? (
                  <ul className="list-group">
                    {specialRequests.map((request, index) => (
                      <li key={index} className="list-group-item">
                        <strong>{request.name}:</strong> {request.request}
                      </li>
                    ))}
                  </ul>
                ) : (
                  <div className="alert info">
                    No special dietary requests submitted.
                  </div>
                )}
              </div>
            </div>
          </div>
        </div>
      </>
    );
  };

  // Session Statistics Tab
  const renderSessionStats = () => {
    if (!sessionStats.length) return null;

    return (
      <>
        <div className="d-flex justify-content-between mb-3">
          <h4>Session Statistics</h4>
          <button 
            className="btn outline-success" 
            onClick={() => handleExportData(sessionStats, 'session-statistics')}
            disabled={exportLoading}
          >
            {exportLoading ? <div className="spinner-sm" /> : 'Export to Excel'}
          </button>
        </div>

        <div className="table-responsive">
          <table className="table striped hover">
            <thead>
              <tr>
                <th>Session</th>
                <th>Time</th>
                <th>Room</th>
                <th>Registered</th>
                <th>Capacity</th>
                <th>Fill Rate</th>
                <th>Status</th>
              </tr>
            </thead>
            <tbody>
              {sessionStats.map(session => {
                const fillRate = (session.registered / session.capacity) * 100;
                let statusClass = 'success';
                if (fillRate > 95) statusClass = 'danger';
                else if (fillRate > 75) statusClass = 'warning';

                return (
                  <tr key={session.session_id}>
                    <td>{session.title}</td>
                    <td>{new Date(session.time).toLocaleString()}</td>
                    <td>{session.room}</td>
                    <td>{session.registered}</td>
                    <td>{session.capacity}</td>
                    <td>
                      <div className="progress-bar-container">
                        <div 
                          className={`progress-bar progress-${statusClass}`}
                          style={{width: `${fillRate}%`}} 
                        />
                      </div>
                      {fillRate.toFixed(1)}%
                    </td>
                    <td>
                      <span className={`badge ${statusClass}`}>
                        {fillRate >= 100 ? 'Full' : fillRate > 75 ? 'Filling Up' : 'Available'}
                      </span>
                    </td>
                  </tr>
                );
              })}
            </tbody>
          </table>
        </div>
      </>
    );
  };

  // VIP Travel Tab
  const renderVIPTravel = () => {
    if (!vipTravel.vipTravel.length && !vipTravel.issues.length) return null;

    return (
      <>
        <div className="d-flex justify-content-between mb-3">
          <h4>VIP Travel Arrangements</h4>
          <button 
            className="btn outline-success" 
            onClick={() => handleExportData(vipTravel.vipTravel, 'vip-travel-arrangements')}
            disabled={exportLoading}
          >
            {exportLoading ? <div className="spinner-sm" /> : 'Export to Excel'}
          </button>
        </div>

        {vipTravel.issues.length > 0 && (
          <div className="alert danger mb-4">
            <h5>VIP Travel Issues Requiring Attention</h5>
            <ul className="issue-list">
              {vipTravel.issues.map((issue, index) => (
                <li key={index}>
                  <button 
                    className="btn link text-left"
                    onClick={() => handleIssueClick(issue)}
                  >
                    <strong>{issue.vip_name}</strong>: {issue.issue_summary}
                  </button>
                </li>
              ))}
            </ul>
          </div>
        )}

        <div className="table-responsive">
          <table className="table striped hover">
            <thead>
              <tr>
                <th>VIP Name</th>
                <th>Flight Details</th>
                <th>Accommodation</th>
                <th>Special Requests</th>
                <th>Status</th>
              </tr>
            </thead>
            <tbody>
              {vipTravel.vipTravel.map(vip => (
                <tr key={vip.user_id}>
                  <td>
                    <strong>{vip.first_name} {vip.last_name}</strong><br />
                    <small>{vip.email}</small>
                  </td>
                  <td>
                    <strong>Arrival:</strong> {vip.arrival_airline} {vip.arrival_flight}<br />
                    {new Date(vip.arrival_time).toLocaleString()}<br />
                    <strong>Departure:</strong> {vip.departure_airline} {vip.departure_flight}<br />
                    {new Date(vip.departure_time).toLocaleString()}
                  </td>
                  <td>
                    {vip.hotel}<br />
                    {vip.hotel_address}<br />
                    {vip.check_in} to {vip.check_out}
                  </td>
                  <td>
                    {vip.special_requests || 'None'}
                  </td>
                  <td>
                    <span className={`badge ${vip.has_issues ? 'danger' : 'success'}`}>
                      {vip.has_issues ? 'Issues' : 'Confirmed'}
                    </span>
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      </>
    );
  };

  // Issue Modal
  const renderIssueModal = () => {
    if (!selectedIssue) return null;

    return (
      <div className={`modal ${showIssueModal ? 'show' : ''}`}>
        <div className="modal-dialog">
          <div className="modal-content">
            <div className="modal-header">
              <h5 className="modal-title">VIP Travel Issue</h5>
              <button 
                type="button" 
                className="btn-close" 
                onClick={() => setShowIssueModal(false)}
              />
            </div>
            <div className="modal-body">
              <h5>{selectedIssue.vip_name}</h5>
              <p><strong>Issue:</strong> {selectedIssue.issue_summary}</p>
              <div className="alert warning">
                <strong>Issue Details:</strong><br />
                {selectedIssue.issue_details}
              </div>

              <h6>Contact Information</h6>
              <ul className="list-group">
                <li className="list-group-item">
                  <strong>Email:</strong> {selectedIssue.email}
                </li>
                <li className="list-group-item">
                  <strong>Phone:</strong> {selectedIssue.phone || 'Not provided'}
                </li>
                <li className="list-group-item">
                  <strong>Travel Agent:</strong> {selectedIssue.travel_agent || 'N/A'}
                </li>
              </ul>
            </div>
            <div className="modal-footer">
              <button 
                type="button" 
                className="btn secondary"
                onClick={() => setShowIssueModal(false)}
              >
                Close
              </button>
              <button 
                type="button" 
                className="btn primary"
              >
                Mark as Resolved
              </button>
            </div>
          </div>
        </div>
      </div>
    );
  };

  if (loading) {
    return (
      <div className="loading-container">
        <div className="spinner" />
        <p>Loading dashboard data...</p>
      </div>
    );
  }

  return (
    <div className="container admin-dashboard">
      <div className="header">
        <h2>Conference Admin Dashboard</h2>
        <div className="header-actions">
          <button className="btn primary" onClick={fetchDashboardData}>
            Refresh Data
          </button>
        </div>
      </div>

      {renderAttendanceStats()}

      <div className="card">
        <div className="card-header">
          <ul className="nav nav-tabs">
            <li className="nav-item">
              <button 
                className={`nav-link ${activeTab === 'attendees' ? 'active' : ''}`}
                onClick={() => setActiveTab('attendees')}
              >
                Attendees
              </button>
            </li>
            <li className="nav-item">
              <button 
                className={`nav-link ${activeTab === 'dietary' ? 'active' : ''}`}
                onClick={() => setActiveTab('dietary')}
              >
                Dietary Requirements
              </button>
            </li>
            <li className="nav-item">
              <button 
                className={`nav-link ${activeTab === 'sessions' ? 'active' : ''}`}
                onClick={() => setActiveTab('sessions')}
              >
                Sessions
              </button>
            </li>
            <li className="nav-item">
              <button 
                className={`nav-link ${activeTab === 'vip' ? 'active' : ''}`}
                onClick={() => setActiveTab('vip')}
              >
                VIP Travel
                {vipTravel.issues.length > 0 && (
                  <span className="badge danger ms-2">
                    {vipTravel.issues.length}
                  </span>
                )}
              </button>
            </li>
          </ul>
        </div>
        <div className="card-body">
          {activeTab === 'attendees' && renderAttendeesList()}
          {activeTab === 'dietary' && renderDietaryRequirements()}
          {activeTab === 'sessions' && renderSessionStats()}
          {activeTab === 'vip' && renderVIPTravel()}
        </div>
      </div>

      {renderIssueModal()}
    </div>
  );
};

export default AdminDashboard;
