import React, { useState } from "react";
import "./styles/TravelRegistrationForm.css";

const TravelRegistrationForm = () => {
  const [travelMethod, setTravelMethod] = useState("Flight");
  const [flightNumber, setFlightNumber] = useState("");
  const [airline, setAirline] = useState("");
  const [departureAirport, setDepartureAirport] = useState("");
  const [arrivalAirport, setArrivalAirport] = useState("");
  const [arrivalTime, setArrivalTime] = useState("");
  const [seatPreference, setSeatPreference] = useState("");
  const [additionalNotes, setAdditionalNotes] = useState("");

  const handleSubmit = (e) => {
    e.preventDefault();
    console.log({ travelMethod, flightNumber, airline, departureAirport, arrivalAirport, arrivalTime, seatPreference, additionalNotes });
  };

  return (
    <div className="container">
      <h2 className="title">Travel Registration</h2>
      <form onSubmit={handleSubmit}>
        <label>Travel Method</label>
        <select
          className="input"
          value={travelMethod}
          onChange={(e) => setTravelMethod(e.target.value)}
        >
          <option value="Flight">Flight</option>
          <option value="Train">Train</option>
          <option value="Car">Car</option>
        </select>

        {travelMethod === "Flight" && (
          <>
            <label>Flight Number</label>
            <input
              type="text"
              className="input"
              value={flightNumber}
              onChange={(e) => setFlightNumber(e.target.value)}
              placeholder="Enter flight number"
              required
            />

            <label>Airline</label>
            <input
              type="text"
              className="input"
              value={airline}
              onChange={(e) => setAirline(e.target.value)}
              placeholder="Enter airline name"
              required
            />

            <label>Departure Airport</label>
            <input
              type="text"
              className="input"
              value={departureAirport}
              onChange={(e) => setDepartureAirport(e.target.value)}
              placeholder="Enter departure airport"
              required
            />

            <label>Arrival Airport</label>
            <input
              type="text"
              className="input"
              value={arrivalAirport}
              onChange={(e) => setArrivalAirport(e.target.value)}
              placeholder="Enter arrival airport"
              required
            />

            <label>Arrival Time</label>
            <input
              type="datetime-local"
              className="input"
              value={arrivalTime}
              onChange={(e) => setArrivalTime(e.target.value)}
              required
            />

            <label>Seat Preference</label>
            <input
              type="text"
              className="input"
              value={seatPreference}
              onChange={(e) => setSeatPreference(e.target.value)}
              placeholder="Enter seat preference (optional)"
            />
          </>
        )}

        <label>Additional Notes</label>
        <textarea
          className="input"
          value={additionalNotes}
          onChange={(e) => setAdditionalNotes(e.target.value)}
          placeholder="Enter any additional travel notes"
        />

        <button type="submit" className="button register">Register Travel</button>
      </form>
    </div>
  );
};

export default TravelRegistrationForm;
